// why did i make this extension man :sob:
// epic dumbutils logo
const icon = "data:image/svg+xml;base64,"
class DumbUtils {
  getInfo() {
    return {
      id: 'dumbutils',
      name: 'DumbUtils',
      color1: '#570098',
      color2: '#290057',
      color3: '#1e0040',
      // logo
      blockIconURI: icon,
      menuIconURI: icon,
      blocks: [
	{
	  opcode: 'rpbtof',
    	  blockType: Scratch.BlockType.BOOLEAN,
          text: 'randomly pick between true or false'
	},
	{
	  opcode: 'yearequals',
    	  blockType: Scratch.BlockType.BOOLEAN,
	  text: 'is it [YEAR]?',
	  arguments: {
	   YEAR: {
		type: Scratch.ArgumentType.NUMBER,
		defaultValue: 2025
		 }
	  }
	},
	{
          opcode: 'divideself',
	  blockType: Scratch.BlockType.REPORTER,
	  text: 'divide [NUM] by itself',
	  arguments: {
	    NUM: {
		type: Scratch.ArgumentType.NUMBER,
		defaultValue: 1
		 }
	  }
	},
	{
	  opcode: 'repeatstring',
	  blockType: Scratch.BlockType.REPORTER,
	  text: 'repeat [STR] [NUM] times',
	  arguments: {
	    STR: {
	      type: Scratch.ArgumentType.STRING,
	      defaultValue: 'hello '
	    },
	    NUM: {
	      type: Scratch.ArgumentType.NUMBER,
	      defaultValue: 5
	    }
	  }
	},
	{
	  opcode: 'turntrue',
	  blockType: Scratch.BlockType.BOOLEAN,
	  text: 'turn [BOOL] true',
	  arguments: {
	    BOOL: {
	      type: Scratch.ArgumentType.BOOLEAN,
	    }
	  }
	}
      ]
  }
  }
  rpbtof() {
	 return Math.random() >= 0.5;
  }
  yearequals(args) {
  	return args.YEAR === new Date().getFullYear();
  }
  divideself(args) {
  	if (args.NUM === 0) {
  		return "aint no way you gonna break the universe ðŸ’€";
	} else {
		return args.NUM / args.NUM;
	}
  }
  repeatstring(args) {
    if (typeof args.STR === "number") {
		return args.STR.toString().repeat(args.NUM)	
	} else {
		return args.STR.repeat(args.NUM)
	}
  }
  turntrue(args) {
    return true
  }
}

Scratch.extensions.register(new DumbUtils());
